import { combineReducers } from "redux";

const tabsReducer = () => {
    return [
        { tabName: 'tasks' }, 
        { tabName: 'goals' }, 
        { tabName: 'reminders' }
    ]
}

const selectedTabReducer = (selectedTab='Tasks', action) => {
    if (action.type === 'TABLE_SELECTED') {
        return action.payload;
    }

    return selectedTab;
}

export default combineReducers({ 
    tabs: tabsReducer, 
    selectedTab: selectedTabReducer,
})
