import db from '../../apis/db'

export const selectTab = tab => {
  return {
    type: 'TAB_SELECTED',
    payload: tab,
  }
}

export const selectTask = task => {
  return {
    type: 'TASK_SELECTED',
    payload: task,
  }
}

export const fetchTasks = () => {
   const promise =  db.get('/task')

  return {
    type: 'FETCH_TASKS',
    payload: promise
  }
}
