import useState from 'react';
import { connect } from 'react-redux'
import { Button, Modal, Icon, TextArea } from 'semantic-ui-react'
import axios from 'axios'



const EditTask = (props) => {

    // const [taskTitle, setTaskTitle] = useState('')

    taskRoutes.route("/update/:id").post(function (req, response) {
        let db_connect = dbo.getDb();
        let myquery = { _id: ObjectId( req.params.id )};
        let newvalues = {
          $set: {
            taskTitle: req.body.taskTitle,
            taskStartTime: req.body.taskStartTime,
            taskEndTime: req.body.taskEndTime,
            notes: req.body.notes,
          },
        };
        db_connect
          .collection("tasks")
          .updateOne(myquery, newvalues, function (err, res) {
            if (err) throw err;
            console.log("1 document updated");
            response.json(res);
          });
      });
  return (
    <Modal
      trigger={
        <Button className="ui small teal icon">
          <i className="pencil icon" />
        </Button>
        // <CreateEditTasks />
      }
      header="Edit/Delete Task"
      content={props.selectedTask}
      actions={['Snooze', { key: 'done', content: 'Done', positive: true }]}
    >
         <Modal.Header className="ui centered teal">Edit Task</Modal.Header>
      <Modal.Content image>
        <Modal.Description>
          <div className="ui raised segment">
            <form className="ui form">
              <div className="field">
                <label>
                  <a href="/" className="ui teal left ribbon label">
                    Task
                  </a>
                </label>
                <input
                  type="text"
                  name="task-name"
                  placeholder=""
                  // onChange={e => setTask(e.target.value)}
                  value={props.selectedTask.title}
                />
                {/* <TextArea>{props.selectedTask.title}</TextArea>  */}
              </div>
              <div className="field">
                <label>
                  <a href="/" className="ui teal left ribbon label">
                    Time
                  </a>
                </label>
                <div className="ui three column stackable padded center aligned grid">
                  <div className="seven wide column">
                    <input
                      type="time"
                      name="task-time"
                      placeholder="Task Time"
                      // onChange={e => setFromTime(e.target.value)}
                      value={props.selectedTask.startTime}
                    />
                  </div>
                  <div className="two wide column ui horizontal divider">
                    To
                  </div>
                  <div className="seven wide column">
                    <input
                      type="time"
                      name="task-time"
                      placeholder="Task Time"
                      // onChange={e => setToTime(e.target.value)}
                      value={props.selectedTask.endTime}
                    />
                  </div>
                </div>
              </div>

              <div className="field">
                <div className="field">
                  <label>
                    <a href="/" className="ui teal left ribbon label">
                      Notes
                    </a>
                  </label>
                  <div className="ui loading icon input">
                    <TextArea
                      placeholder=""
                      value={props.selectedTask.notes}
                    ></TextArea>
                  </div>
                  <div className="ui padded equal width grid"></div>
                </div>
              </div>
              <div className="ui two column padded centered grid ">
                <Modal.Actions>
                  <Button
                    //   onClick={cancelTaskAdd}
                    className="center"
                    basic
                    color="teal"
                  >
                    <Icon name="close" />
                    Cancel
                  </Button>
                  <Button
                    //   onClick={() => newTaskAdded()}
                    color="teal"
                    className="four wide column"
                  >
                    Save <Icon name="right chevron" />
                  </Button>
                </Modal.Actions>
              </div>
            </form>
          </div>
        </Modal.Description>
      </Modal.Content>
    </Modal>
  )
}

const mapStateToProps = state => {
  return { selectedTask: state.selectedTask }
}

export default connect(mapStateToProps)(EditTask)
