const LoginPage = () => {
  const [signupVisible, setSignupVisible] = useState(false)

  return (









        <div className="ui teal container  middle aligned" style={{minHeight: '100vh'}}>
              {(signupVisible && <Signup />) || <Login />}
              New User? 
              <span
                style={{color: 'teal'}}
                onClick={() => setSignupVisible(!signupVisible)}
                className="ui basic teal"
              >
                <a href="#" style={{color: 'teal', padding: '10px'}}  onClick={() => setSignupVisible(!signupVisible)}>
                Click here
                </a>
              </span>

            </div>




  )
}

export default LoginPage
