import { useState } from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

export const Menu = () => {
  const [taskName, setTaskName] = useState('active')
  const [goalsName, setGoalsName] = useState('')
  const [remindersName, setRemindersName] = useState('')

  const taskTab = () => {
    setTaskName('active')
    setGoalsName('')
    setRemindersName('')
  }

  function goalTab() {
    setGoalsName('active')
    setTaskName('')
    setRemindersName('')
  }

  function remindTab() {
    setRemindersName('active')
    setTaskName('')
    setGoalsName('')
  }

  const renderMenu = () => {
    return props.tabs.map(tab => {
      return (
        <Link to={`/${tab.tabName}`} key={tab.tabName} >
          {tab.tabName}
        </Link>
      )
    })
  }

  return (
    <>
      <div className="ui top attached tabular menu">
        <Link to="/tasks" className={`item ${taskName}`} onClick={taskTab}>
          Tasks
        </Link>
        <Link to="/goals" className={`item ${goalsName}`} onClick={goalTab}>
          Goals
        </Link>
        <Link
          to="/reminders"
          className={`item ${remindersName}`}
          onClick={remindTab}
        >
          Reminders
        </Link>
      </div>
      {renderMenu()}
    </>
  )
}

const mapStateToProps = state => {
  return { tabs: state.tabs, goals: state.goals }
}

export default connect(mapStateToProps)(Menu)
