//         {id: '', title: 'study', startTime: '12:30', endTime: '01:00', notes: 'study mindset'}
//     ]
// }
const initState = {
    taskList: [
        {id: '', title: '', startTime: '', endTime: '', notes: ''},
    ]
}

const allData = (state=initState, action) => {
    return state
}

export default combineReducers({ 
    tabs: tabsReducer, 
    selectedTab: selectedTabReducer,
    selectedTask: selectedTaskReducer,
    taskList: allData,
    auth: auth
})
