const CreateEditTasks = () => {
    return (
        <tr key={tsk.id} onClick={() => props.selectTask(tsk)}>
          <td className="collapsing ">
              <div className="ui fitted slider checkbox checked">
                <input className=" ui right floated" type="checkbox" />{' '}
                <label></label>
              </div>
            </td>
            <td style={{ textAlign: 'center' }}>{tsk.title}</td>
            <td style={{ textAlign: 'center' }}>{tsk.startTime}-{tsk.endTime}</td>
            <td style={{ textAlign: 'center' }}>{tsk.notes}</td>
        </tr>
    )
}

const mapStateToProps = (state) => {
    return  { selectedTask: state.selectedTask }
   }

