import React, { useState } from 'react'
import { Button, Icon, Modal } from 'semantic-ui-react'

const AddTask = () => {
  const [firstOpen, setFirstOpen] = React.useState(false)
  const [secondOpen, setSecondOpen] = React.useState(false)

  const newTaskAdded = () => {
    setFirstOpen(true)
    setSecondOpen(true)

    setTimeout(() => {
      setSecondOpen(false)
      setFirstOpen(false)
    }, 1200)
  }
  return (
    // <div class="ui active fullscreen modal">
    //   <div class="header centered">Add New Task</div>
    //   <div class="content">
    //     <p></p>
    //   </div>
    //   <div class="actions">
    //     <div class="ui approve button">Approve</div>
    //     <div class="ui button">Neutral</div>
    //     <div class="ui cancel button">Cancel</div>
    //   </div>
    // </div>

    <>
      <Button
        className="ui small teal labeled icon button"
        onClick={() => setFirstOpen(true)}
      >
        <i className="plus icon"></i> Add New Task
      </Button>

      <Modal
        onClose={() => setFirstOpen(false)}
        onOpen={() => setFirstOpen(true)}
        open={firstOpen}
      >
        <Modal.Header>Add New Task</Modal.Header>
        <Modal.Content image>
          <div className="image">
            <Icon name="right arrow" />
          </div>
          <Modal.Description>
            <div class="column">
              <div class="ui segment">
                <a class="ui teal left ribbon label">Task</a>
                <div class="ui input">
                  <input type="text" placeholder="What do you need to do?" />
                </div>
                <a class="ui teal left ribbon label">Time</a>
                <div class="ui input">
                  <input type="text" placeholder="When do you plan to do it?" />
                </div>
                <a class="ui teal left ribbon label">Priority</a>
                <div class="ui input">
                  <input type="text" placeholder="How important is it?" />
                </div>
              </div>
            </div>
          </Modal.Description>
        </Modal.Content>
        <Modal.Actions>
          <Button onClick={() => newTaskAdded()} primary>
            Proceed <Icon name="right chevron" />
          </Button>
        </Modal.Actions>

        <Modal
          onClose={() => setSecondOpen(false)}
          open={secondOpen}
          size="small"
        >
          <Modal.Header>Success</Modal.Header>
          <Modal.Content style={{ backgroundColor: 'rgba(30, 250, 22, 0.2)' }}>
            <p>Task added successfully!</p>
          </Modal.Content>
          <Modal.Actions>
            <Button
              icon="check"
              content="All Done"
              onClick={() => setSecondOpen(false)}
            />
          </Modal.Actions>
        </Modal>
      </Modal>
    </>
  )
}

export default AddTask
