import {useState} from 'react'
import { Link } from 'react-router-dom'

// import Tasks from './Tasks'
// import Reminders from './Reminders'
// import Goals from './Goals'

const Menu = (props) => {
    const [taskName, setTaskName] = useState('')
    const [goalsName, setGoalsName] = useState('')
    const [remindersName, setRemindersName] = useState('')

    function changeTab() {
        if(props.name){
            setTaskName('active')
            setGoalsName('')
            setRemindersName('')
        } else if(props.goals) {
            setGoalsName('active')
            setTaskName('')
            setRemindersName('')
        } else if(props.reminders) {
            setRemindersName('active')
            setTaskName('')
            setGoalsName('')
        }
        console.log(props.name)
    }
  return (
    <div className="ui top attached tabular menu">
      <Link to="/" className={`item ${taskName}`} onClick={changeTab} >
        Tasks
      </Link>
      <Link to="/goals" className={`item ${goalsName}`} onClick={changeTab}  >
       Goals
      </Link>
      <Link  to="/reminders" className={`item ${remindersName}`} onClick={changeTab} >
      Reminders
      </Link>
    </div>
  )
}

export default Menu
