import {useState} from 'react'
import { Link } from 'react-router-dom'
import logo from '../media/hellbentLogo.png'

// import Tasks from './Tasks'
// import Reminders from './Reminders'
// import Goals from './Goals'

const Menu = (props) => {
    const [taskName, setTaskName] = useState('active')
    const [goalsName, setGoalsName] = useState('')
    const [remindersName, setRemindersName] = useState('')

    const taskTab = () => {
            setTaskName('active')
            setGoalsName('')
            setRemindersName('')
    }

    function goalTab() {
            setGoalsName('active')
            setTaskName('')
            setRemindersName('')
    }

    function remindTab() {
            setRemindersName('active')
            setTaskName('')
            setGoalsName('')
        }

  return (
      <>
    <div className="ui segment">
          <Link to="/" ><img className="ui centered small image" src={logo}></img></Link>
    <div className="ui top attached tabular menu">
      <Link to="/" className={`item ${taskName}`} onClick={taskTab} >
        Tasks
      </Link>
      <Link to="/goals" className={`item ${goalsName}`} onClick={goalTab}  >
       Goals
      </Link>
      <Link  to="/reminders" className={`item ${remindersName}`} onClick={remindTab} >
      Reminders
      </Link>
    </div>
    </div>
    </>
  )
}

export default Menu
