import { useState } from 'react'
import { Link } from 'react-router-dom'

import Tasks from '../pages/Tasks'
import Reminders from '../pages/Reminders'
import Goals from '../pages/Goals'

const Menu = props => {
  const [taskName, setTaskName] = useState('active')
  const [goalsName, setGoalsName] = useState('')
  const [remindersName, setRemindersName] = useState('')

  const taskTab = () => {
    setTaskName('active')
    setGoalsName('')
    setRemindersName('')
  }

  function goalTab() {
    setGoalsName('active')
    setTaskName('')
    setRemindersName('')
  }

  function remindTab() {
    setRemindersName('active')
    setTaskName('')
    setGoalsName('')
  }

  return (
    <>
      <div className="ui top attached tabular menu">
        <Link to="/tasks" className={`item ${taskName}`} onClick={taskTab}>
          Tasks
        </Link>
        <Link to="/goals" className={`item ${goalsName}`} onClick={goalTab}>
          Goals
        </Link>
        <Link
          to="/reminders"
          className={`item ${remindersName}`}
          onClick={remindTab}
        >
          Reminders
        </Link>
      </div>

    </>
  )
}

export default Menu
