import { combineReducers } from "redux";
import auth from './auth'

const tabsReducer = () => {
    return [
        { tasks: 'active' }, 
        { goals: 'goals' }, 
        { notes: 'notes' }
    ]
}

const selectedTabReducer = (selectedTab='tasks', action) => {
    if (action.type === 'TAB_SELECTED') {
        return action.payload;
    }

    return selectedTab;
}

const selectedTaskReducer = (selectedTask='null', action) => {
    if (action.type === 'TASK_SELECTED') {
        return action.payload;
    }

    return selectedTask;
}

const initState = {
    taskList: [
        {id: '1', title: 'work on redux', startTime: '10:30', endTime: '11:00', notes: 'Dont procrastinate'},
        {id: '2', title: 'take a break', startTime: '11:00', endTime: '12:00', notes: 'Have fun with the kids'},
        {id: '3', title: 'study', startTime: '12:30', endTime: '01:00', notes: 'study mindset'}
    ]
}

const allData = (state=initState, action) => {
    return state
}

export default combineReducers({ 
    tabs: tabsReducer, 
    selectedTab: selectedTabReducer,
    selectedTask: selectedTaskReducer,
    taskList: allData,
    auth: auth
})
