import { x } from 'keyboard-key'
import { useState } from 'react'
import { Grid, Button, Icon, Header, Input, Image, Divider } from 'semantic-ui-react'

import img from '../media/hellbentLogo.png'
import Auth from './Auth'

const Signup = () => {
  const [name, setName] = useState('')
  const [password, setPassword] = useState('')
  const [email, setEmail] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const [visible, setVisible] = useState('password')
  console.log(name, password, email, confirmPassword, document.getElementById("myPassword"))

  const pass = document.getElementById("myPassword").type
  console.log(pass)

  const showPass = (pass) => {
    pass === "text" ? pass = "text" : pass = "password"
  }

  // const showPassword= () => {
  //   var pass = document.getElementById("myPassword")
  //   console.log(pass)

  //   // pass.type === 'password' ? pass.type = 'text' : pass.type = 'password'
  // // //   var x = document.getElementById("myInput");
  // if (pass.type === "password") {
  //   pass.type = "text";
  // } else {
  //   pass.type = "password";
  // }
  // }

  return (
    <>
      <Divider horizontal>
        <Header as="h4" color="teal">
          Signup
        </Header>
      </Divider>
      <div style={{ position: 'relative', top: '10%', width: '100%' }}>
        <Grid.Column textAlign="center">
          <div className="ui grid centered">
            <div className="ui row">
              <Input
                action={{
                  icon: 'user',
                  color: 'teal',
                }}
                onChange={(e) => {setName(e.target.value)}}
                actionPosition="left"
                type="text"
                className="ui input"
                placeholder="Name"
              />
            </div>
            <div className="ui row">
              <Input
                action={{
                  icon: 'envelope',
                  color: 'teal',
                }}
                id="password"
                onChange={(e) => {setEmail(e.target.value)}}
                actionPosition="left"
                type="text"
                className="ui input"
                placeholder="Email"
              />
            </div>
            <div className="ui row">
              <Input
                action={{
                  icon: 'lock',
                  color: 'teal',
                }}
                onChange={(e) => {setPassword(e.target.value)}}
                actionPosition="left"
                id="myPassword"
                type="password"
                className="ui input"
                placeholder="Password"
              />
            </div>

            <div className="ui row">
              <Input
                action={{
                  icon: 'eye',
                  color: 'teal',
                }}
                onChange={(e) => {setConfirmPassword(e.target.value)}}
                actionPosition="left"
                type="password"
                display="FakePSW"
                className="ui input"
                id="password"
                placeholder="Confirm Password"
                onClick={showPass()}
              />
            </div>
            <div className="ui row">
              <Button color="teal">Sign up</Button>
              <Auth />
            </div>
          </div>
        </Grid.Column>
      </div>
    </>
  )
}

export default Signup
