  return (
    <>
    {/* {console.log(props)} */}
      <div className="ui top attached tabular menu"> 
        <Link to="/tasks" className={`item ${tabSelection()}`} onClick={() => props.selectTab('tasks')}>
          Tasks
        </Link>
        <Link to="/goals" className={`item ${props.tab === 'goals' ? 'active' : ''}`} onClick={() => props.selectTab('goals')}>
          Goals
        </Link>
        <Link
          to="/reminders"
          className={`item ${props.selectedTab === 'notes' ? 'active' : ''}`} onClick={() => props.selectTab('notes')}>
         Notes
        </Link> 

        {/* {console.log(props.tabs[1].tabName, props.selectTab )} */}

        {console.log(props.selectTab)}
       </div>
    </>
  )
}

const mapStateToProps = state => {
  console.log(state)
  return { tab: state.selectedTab, tasks: state.tasks }
}

export default connect(mapStateToProps, {selectTab})(Menu)
