export const selectTab = tab => {
  return {
    type: 'TAB_SELECTED',
    payload: tab,
  }
}

export const selectTask = task => {
  return {
    type: 'TASK_SELECTED',
    payload: task,
  }
}

export const fetchTasks = tasks => {
  return {
    type: 'FETCH_TASKS',
  }
}
