import { x } from 'keyboard-key'
import { useState } from 'react'
import { Grid, Button, Icon, Header, Input, Image, Divider } from 'semantic-ui-react'

import img from '../media/hellbentLogo.png'
import Auth from './Auth'

const Signup = () => {
  const [name, setName] = useState('')
  const [password, setPassword] = useState('')
  const [email, setEmail] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')
  const [passwordVisible, setPasswordVisible] = useState(false)
  const [confirmPasswordVisible, setConfirmPasswordVisible] = useState(false)
  console.log(name, password, email, confirmPassword, document.getElementById("myPassword"))




  // const showPass = (pass) => {
  //   var pass = document.getElementById("myPassword")
  //   pass.type === "text" ? pass.type = "text" : pass.type = "password"
  // }

  // const showPassword= () => {
  //   var pass = document.getElementById("myPassword")
  //   console.log(pass)

  //   // pass.type === 'password' ? pass.type = 'text' : pass.type = 'password'
  // // //   var x = document.getElementById("myInput");
  // if (pass.type === "password") {
  //   pass.type = "text";
  // } else {
  //   pass.type = "password";
  // }
  // }

  return (
    <>
      <Divider horizontal>
        <Header as="h4" color="teal">
          Signup
        </Header>
      </Divider>
      <div style={{ position: 'relative', top: '10%', width: '100%' }}>
        <Grid.Column textAlign="center">
          <div className="ui grid centered">
            <div className="ui row">
              <Input
                action={{
                  icon: 'user',
                  color: 'teal',
                }}
                onChange={(e) => {setName(e.target.value)}}
                actionPosition="left"
                type="text"
                className="ui input"
                placeholder="Name"
              />
            </div>
            <div className="ui row">
              <Input
                action={{
                  icon: 'envelope',
                  color: 'teal',
                }}
                id="password"
                onChange={(e) => {setEmail(e.target.value)}}
                actionPosition="left"
                type="text"
                className="ui input"
                placeholder="Email"
              />
            </div>
            <div className="ui row">
              <Input
                action={{
                  icon: 'lock',
                  color: 'teal',
                }}
                onChange={(e) => {setPassword(e.target.value)}}
                actionPosition="left"
                id="myPassword"
                type={(passwordVisible && "text") || "password"}
                className="ui input"
                placeholder="Password"
                onClick={() => setPasswordVisible(!passwordVisible)}
              />
            </div>

            <div className="ui row">
              <Input
              icon = {<Icon name='eye' circular link onClick={() => setConfirmPasswordVisible(!confirmPasswordVisible)}/>}
                action={{
                  icon: 'lock',
                  color: 'teal',
                }}
                onChange={(e) => {setConfirmPassword(e.target.value)}}
                actionPosition="left"
                type={(confirmPasswordVisible && "text") || "password"}
                display="FakePSW"
                className="ui input"
                id="myPassword"
                placeholder="Confirm Password"
                onClick={() => setConfirmPasswordVisible(!confirmPasswordVisible)}
              />
            </div>
            <div className="ui row">
              <Button color="teal">Sign up</Button>
              <Auth />
            </div>
          </div>
        </Grid.Column>
      </div>
    </>
  )
}

export default Signup
