import React, { useState } from 'react'
import { Button, Icon, Modal } from 'semantic-ui-react'

const AddTask = () => {
  const [firstOpen, setFirstOpen] = React.useState(false)
  const [secondOpen, setSecondOpen] = React.useState(false)
  const [task, setTask] = useState('')
  const [toTime, setToTime] = useState('')
  const [fromTime, setFromTime] = useState('')
  const [priority, setPriority] = useState('low')

  // const onTaskChange = e => {

  //   console.log(task)
  //   setTask(e.target.value)
  // }

  // const onPriorityChange = e => {
  //   setPriority(e.target.value)
  // }

  // const onFromTimeChange = (e) => {
  //   setFromTime(e.target.value)
  // }

  // const onToTimeChange = (e) => {
  //   setToTime(e.target.value)
  // }

  // const onFormSubmit = e => {
  //   e.preventDefault()
  // }

  const cancelTaskAdd = () => {
    setSecondOpen(false)
    setFirstOpen(false)
  }

  const newTaskAdded = () => {
    setFirstOpen(true)
    setSecondOpen(true)

    setTimeout(() => {
      setSecondOpen(false)
      setFirstOpen(false)
    }, 2000)
  }
  return (
    <>
      <Button
        className="ui small teal labeled icon button"
        onClick={() => setFirstOpen(true)}
      >
        <i className="plus icon" /> Add New Task
      </Button>

      <Modal
        onClose={() => setFirstOpen(false)}
        onOpen={() => setFirstOpen(true)}
        open={firstOpen}
      >
        <Modal.Header className="ui centered teal">Add New Task</Modal.Header>
        <Modal.Content image>
          <Modal.Description>
            <div className="ui raised segment">
              <form className="ui form">
                <div className="field">
                  <label>
                    <a href="" className="ui teal left ribbon label">Task</a>
                  </label>
                  <input
                    type="text"
                    name="task-name"
                    placeholder="Task Name"
                    onChange={e => setTask(e.target.value)}
                    value={task}
                  />
                </div>
                <div className="field">
                  <label>
                    <a className="ui teal left ribbon label">Time</a>
                  </label>
                  <div className="ui three column stackable padded center aligned grid">
                    <div className="seven wide column">
                      <input
                        type="time"
                        name="task-time"
                        placeholder="Task Time"
                        onChange={e => setFromTime(e.target.value)}
                        value={fromTime}
                      />
                    </div>
                    <div className="two wide column ui horizontal divider">To</div>
                    <div className="seven wide column">
                      <input
                        type="time"
                        name="task-time"
                        placeholder="Task Time"
                        onChange={e => setToTime(e.target.value)}
                        value={toTime}
                      />
                    </div>
                  </div>
                </div>
                <div className="field">
                  <a className="ui teal left ribbon label">Priority </a>


                    <div className="ui padded equal width grid">
                    <div className="grouped fields">
                    <div className="field column">
                      <div className="ui radio checkbox column">
                        <input
                          type="radio"
                          name="priority"
                          checked="default checked"
                          tabIndex="0"
                          className="hidden"
                          onChange={e => setPriority(e.target.value)}
                          value={priority}
                        />
                        <label>Low</label>
                      </div>
                    </div>
                    <div className="field column">
                      <div className="ui radio checkbox column">
                        <input
                          type="radio"
                          name="priority"
                          tabIndex="0"
                          className="hidden"
                          onChange={e => setPriority(e.target.value)}
                          value={priority}
                        />
                        <label>Medium</label>
                      </div>
                    </div>
                    <div className="field column">
                      <div className="ui radio checkbox">
                        <input
                          type="radio"
                          name="priority"
                          tabIndex="0"
                          className="hidden"
                          onChange={e => setPriority(e.target.value)}
                          value={priority}
                        />
                        <label>High</label>
                      </div>
                    </div>
                  </div>
                    </div>

                </div>
                <div className="ui two column padded centered grid ">
                  <Modal.Actions>
                    <Button
                      onClick={cancelTaskAdd}
                      className="center"
                      basic
                      color="teal"
                    >
                      <Icon name="close" />
                      Cancel
                    </Button>
                    <Button
                      onClick={() => newTaskAdded()}
                      color="teal"
                      className="four wide column"
                    >
                      Proceed <Icon name="right chevron" />
                    </Button>
                  </Modal.Actions>
                </div>
              </form>
            </div>
          </Modal.Description>
        </Modal.Content>

        <Modal
          onClose={() => setSecondOpen(false)}
          open={secondOpen}
          size="small"
        >
          <Modal.Content style={{ textAlign: 'center' }}>
            <h2 className="ui centered icon teal header">
              <i className="circular check icon white"></i>
              <div className="content">Task added successfully!</div>
            </h2>
          </Modal.Content>
        </Modal>
      </Modal>
    </>
  )
}

export default AddTask
