import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

import { selectTab } from '../redux/actions'

export const Menu = props => {
  return (
    <>
    {/* {console.log(props)} */}
      <div className="ui top attached tabular menu"> 
        <Link to="/tasks" className={`item `} onClick={() => props.selectTab(props.tab)}>
          Tasks
        </Link>
        <Link to="/goals" className={`item `} onClick={goalTab}>
          Goals
        </Link>
        <Link
          to="/reminders"
          className={`item `}
          onClick={remindTab}
        >
          Reminders
        </Link> 

        {/* {console.log(props.tabs[1].tabName, props.selectTab )} */}

        {console.log(props.tasks)}
       </div>
    </>
  )
}

const mapStateToProps = state => {
  // console.log(state)
  return { tab: state.selectedTab, tasks: state.tasks }
}

export default connect(mapStateToProps, {selectTab})(Menu)
