import { useState, useEffect } from "react"
import { Button, Icon } from "semantic-ui-react"

const GoogleAuth = () => {
 const [isSignedIn, setIsSignedIn] = useState(null)

    useEffect(() => window.gapi.load('client:auth2', () => {
        window.gapi.client.init({
            clientId: '325275291512-jr3142ptf4kib4non2t5cffpu9v9k1th.apps.googleusercontent.com',
            scope: 'email'
        }).then(() => {
            const auth = window.gapi.auth2.getAuthInstance();
            setIsSignedIn({ isSignedIn: auth.isSignedIn.get() })
        })
    }))

    const renderAuthButton = () => {
        if(isSignedIn === null){
            return <div>I dont know</div>
        } else if (isSignedIn) {
            return <div>Signed in!</div>
        } else {
            return <div>NOPE!</div>
        }
    }

    return (
        <Button basic color="teal" onClick={() => renderAuthButton()}><Icon name="google" />Google Login</Button>
    )
}

export default GoogleAuth
