import React, { useState, useEffect } from 'react'
import { Button, Icon, Modal, TextArea } from 'semantic-ui-react'
import axios from 'axios'
import TaskDataService from '../services/taskService'


const AddTask = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')))  
  const [firstOpen, setFirstOpen] = React.useState(false)
  const [secondOpen, setSecondOpen] = React.useState(false)
  const [task, setTask] = useState('')
  const [toTime, setToTime] = useState('')
  const [fromTime, setFromTime] = useState('')
  const [notes, setNotes] = useState('')
  const [taskTitle, setTaskTitle] = useState('')
  const [taskStartTime, setTaskStartTime] = useState('')
  const [taskEndTime, setTaskEndTime] = useState('')

  const [addNewTask, setAddNewTask] = useState ({
    id: user.result.googleId,
    taskTitle: '',
    taskStartTime: '',
    taskEndTime: '',
    notes: ''
  })

  const handleChange = (e) => {
    const {name, value} = e.target

    setAddNewTask(prevAddNewTask => {
      return { 
        ...prevAddNewTask,
        [name]: value
      }
    })
  }

  const handleClick = (e) => {
    e.preventDefault()
    console.log(addNewTask)
  }


  // console.log(addNewTask)

  // const [newTask, setNewTask] = useState(addNewTask)
  // const getTask = id => {
  //   TaskDataService.get(id)
  //   .then(response => {
  //     setTask(response.data)
  //     console.log(response.data)
  //   })
  //   .catch(err => {
  //     console.log(err)
  //   })
  // }

  // useEffect(() => {
  //   getTask()
  // })
  // const onTaskChange = e => {

  //   console.log(task)
  //   setTask(e.target.value)
  // }

  // const onPriorityChange = e => {
  //   setPriority(e.target.value)
  // }

  // const onFromTimeChange = (e) => {
  //   setFromTime(e.target.value)
  // }

  // const onToTimeChange = (e) => {
  //   setToTime(e.target.value)
  // }

  // const onFormSubmit = e => {
  //   e.preventDefault()
  // }

  console.log(task, fromTime, toTime, notes)
  const onAddTask = e => {
    e.preventDefault()

  }

  const cancelTaskAdd = () => {
    setSecondOpen(false)
    setFirstOpen(false)
  }

  const newTaskAdded = () => {
    setFirstOpen(true)
    setSecondOpen(true)

    setTimeout(() => {
      setSecondOpen(false)
      setFirstOpen(false)
    }, 3000)
  }
  return (
    <>
      <Button
        className="ui small teal labeled icon button"
        onClick={() => setFirstOpen(true)}
      >
        <i className="plus icon" /> Add New Task
      </Button>

      <Modal
        onClose={() => setFirstOpen(false)}
        onOpen={() => setFirstOpen(true)}
        open={firstOpen}
      >
        <Modal.Header className="ui centered teal">Add New Task</Modal.Header>
        <Modal.Content image>
          <Modal.Description>
            <div className="ui raised segment">
              <form className="ui form">
                <div className="field">
                  <label>
                    <a href="/" className="ui teal left ribbon label">
                      Task
                    </a>
                  </label>
                  <input
                    type="text"
                    name="taskTitle"
                    placeholder="Task Name"
                    onChange={handleChange}
                    value={addNewTask.taskTitle}
                  />
                </div>
                <div className="field">
                  <label>
                    <a href="/" className="ui teal left ribbon label">
                      Time
                    </a>
                  </label>
                  <div className="ui three column stackable padded center aligned grid">
                    <div className="seven wide column">
                      <input
                        type="time"
                        name="fromTime"
                        placeholder="Task Time"
                        onChange={handleChange}
                        value={addNewTask.fromTime}
                      />
                    </div>
                    <div className="two wide column ui horizontal divider">
                      To
                    </div>
                    <div className="seven wide column">
                      <input
                        type="time"
                        name="toTime"
                        placeholder="Task Time"
                        onChange={handleChange}
                        value={addNewTask.toTime}
                      />
                    </div>
                  </div>
                </div>

                <div className="field">
                  <div className="field">
                    <label>
                      <a href="/" className="ui teal left ribbon label">
                        Notes
                      </a>
                    </label>
                    <div class="ui loading icon input">
                      <TextArea
                        name="notes"
                        placeholder="Add notes about your task..."
                        onChange={handleChange}
                        value={addNewTask.notes}
                      />
                    </div>
                    <div className="ui padded equal width grid"></div>
                  </div>
                </div>
                <div className="ui two column padded centered grid ">
                  <Modal.Actions>
                    <Button
                      onClick={handleClick}
                      className="center"
                      basic
                      color="teal"
                    >
                      <Icon name="close" />
                      Cancel
                    </Button>
                    <Button
                      onClick={onAddTask}
                      color="teal"
                      className="four wide column"
                    >
                      Proceed <Icon name="right chevron" />
                    </Button>
                  </Modal.Actions>
                </div>
              </form>
            </div>
          </Modal.Description>
        </Modal.Content>
        <Modal
          onClose={() => setSecondOpen(false)}
          open={secondOpen}
          size="small"
        >
          <Modal.Content style={{ textAlign: 'center' }}>
            <h2 className="ui centered icon teal header">
              <i className="circular check icon white"></i>
              <div className="content">Task added successfully!</div>
            </h2>
          </Modal.Content>
        </Modal>
      </Modal>
    </>
  )
}

export default AddTask
