import { combineReducers } from "redux";

const tabsReducer = () => {
    return [
        { tabName: 'Tasks' }, 
        { tabName: 'Goals' }, 
        { tabName: 'Reminders' }
    ]
}

const selectedTabReducer = (selectedTab=null, action) => {
    if (action.type === 'TAB_SELECTED') {
        return action.payload;
    }

    return selectedTab;
}

const initState = {
    tasks: []
}

const allData = (state = initState, action) => {
    return state
}

export default combineReducers({ 
    tabs: tabsReducer, 
    selectedTab: selectedTabReducer,
    data: allData,
})
