import {useState} from 'react'
import { Link } from 'react-router-dom'

import Tasks from './Tasks'
import Reminders from './Reminders'
import Goals from './Goals'

const Menu = () => {
    const [className, setClassName] = useState('item')

    function changeTab() {
        if(className === "tasks"){
            setClassName('active')
        } else {
            setClassName('')
        }
    }
  return (
    <div className="ui top attached tabular menu">
      <Link to="/" className={`item tasks ${className}`} onClick={changeTab} >
        Tasks
      </Link>
      <Link to="/reminders" className={`item reminders ${className}`} onClick={changeTab}  >
        Reminders
      </Link>
      <Link  to="/goals" className={`item goals ${className}`} onClick={changeTab} >
        Goals
      </Link>
    </div>
  )
}

export default Menu
