import { useState } from 'react'
import { Grid, Button, Segment, Header, Image, Input } from 'semantic-ui-react'
import Signup from '../components/auth/Signup'
import Login from '../components/auth/Login'
import img from '../media/hellbentLogo.png'

const LoginPage = () => {
  const [signupVisible, setSignupVisible] = useState(false)

  return (
    <div className="ui container centered ">
      <Segment>
      <Image src={img} centered size="medium" />




        <div className="ui">
              {(signupVisible && <Signup />) || <Login />}
              New User{' '}
              <span
                onClick={() => setSignupVisible(!signupVisible)}
                className="ui teal"
              >
                Click here
              </span>
            </div>
      </Segment>
    </div>
  )
}

export default LoginPage
