import { combineReducers } from "redux";
import fetchTasksReducer from "./tasksReducer";
import tabsReducer from "./tabsReducer";
import selectedTabReducer from './selectedTabReducer'
import selectedTaskReducer from './selectedTaskReducer';
import auth from './auth'

const initState = {
    taskList: [
        {id: '1', title: 'work on redux', startTime: '10:30', endTime: '11:00', notes: 'Dont procrastinate'},
        {id: '2', title: 'take a break', startTime: '11:00', endTime: '12:00', notes: 'Have fun with the kids'},
        {id: '3', title: 'study', startTime: '12:30', endTime: '01:00', notes: 'study mindset'}
    ]
}

const allData = (state=initState, action) => {
    return state
}

export default combineReducers({ 
    tabs: tabsReducer, 
    selectedTab: selectedTabReducer,
    selectedTask: selectedTaskReducer,
    taskList: allData,
    auth: auth,
    tasks: tasksReducer
})
