import { useEffect, useState } from 'react'
// import EditTask from '../components/EditTask'
import { selectTask } from '../redux/actions/index'
import AddTask from '../components/AddTask'
import Menu from '../components/Menu'
import BannerImage from '../components/BannerImage'
import { connect } from 'react-redux'

// import TaskDataService from '../services/taskService'

const Tasks = props => {


  useEffect(() => {
    renderTaskList(props.match.params.id)
  }, [props.match.params.id])

  const renderTaskList = () => {

  }

  const renderTasks = () => {
    return props.taskList.taskList.map(task => {
      return (
        <tr key={task.id} onClick={() => props.selectTask(task)}>
          <td className="collapsing ">
            {/* <EditTask /> */}
          </td>
          <td style={{ textAlign: 'center' }}>{task.title}</td>
          <td style={{ textAlign: 'center' }}>
            {task.startTime}-{task.endTime}
          </td>
          <td style={{ textAlign: 'center' }}>{task.notes}</td>
        </tr>
      )
    })
  }

  return (
    <>
      {console.log(props)}
      <BannerImage />
      <Menu />
      <div className="ui bottom attached segment">
        <table className="ui compact teal celled definition table">
          <thead className="full-width">
            <tr>
              <th></th>
              <th style={{ textAlign: 'center' }}>Task</th>
              <th style={{ textAlign: 'center' }}>Time</th>
              <th style={{ textAlign: 'center' }}>Notes</th>
            </tr>
          </thead>
          <tbody>{renderTasks()}</tbody>
          <tfoot className="full-width">
            <tr>
              <th></th>
              <th colSpan="4" style={{ textAlign: 'center' }}>
                <AddTask />

                {/* <div className="ui right floated  buttons">
                <button className="ui teal button">Edit Task</button>
                <div className="or"></div>
                <button className="ui red button">Delete Task</button>
              </div> */}
              </th>
            </tr>
          </tfoot>
        </table>
      </div>
    </>
  )
}

const mapStateToProps = state => {
  return { taskList: state.taskList, selectedTask: state.selectedTask, }
}

export default connect(mapStateToProps, { selectTask })(Tasks)
